import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { Link } from 'react-router-dom';
import $ from 'jquery';
import './Styles/Books.css';

function Books() {
  const [searchKey, setSearchKey] = useState("");
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if(searchKey !== "") {
      console.log("searched");
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err))
    } else {
      console.log("no search");
      Axios.get("https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE"+"&maxResults=40")
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err));
    }

    setTimeout(()=> {
      setIsLoading(false);
    }, 1000);
  }, [searchKey]);

  useEffect(()=> {
    if(localStorage.getItem("searchKey") != undefined){
      setSearchKey(localStorage.getItem("searchKey"));
    }
  })

  const searchBook = () => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
    .then(res=> setBookData(res.data.items))
    .catch(err=>console.log(err))
  }

  return (
    <div className='book-page'>
      { isLoading ? <div><br/><br/><h1>Just grabbing some books...</h1><img id='loading' src={require('../assets/images/loading.gif')} alt='loading gif' /> </div> : 
        <div className='book-results'>
          { 
            bookData.map((book) => {
              let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;
              let bookId = book.id;

              if(cover !== undefined){
                return (
                  <Link key={book.id} to='/bookdetail' state={{id: bookId, page: '/books'}}>
                    <img src={cover} alt='Book cover' />
                  </Link>  
                )
              }
            }) 
          }
        </div>
      }
    </div>
  )
}

export default Books
