import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { useIsAuthenticated } from 'react-auth-kit';
import { useNavigate } from 'react-router-dom';
import { useAuthUser } from 'react-auth-kit';
import './Styles/MyLog.css';

function MyLog() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const navigate = useNavigate();  
  const [bookData, setBookData] = useState([]);
  const [logData, setLogData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [booksLogged, setBooksLogged] = useState();

  useEffect(() => {
    if(isAuthenticated()){
      const usern = authUser().username;
      Axios.post('http://localhost:4000/books', {
        usern: usern
      }).then((response) => {
        if(response.data.message){
          setBooksLogged(false);
          setIsLoading(false);
        } else { 
          setBooksLogged(true);
          setLogData(response.data);
          var href = "https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE"+"&maxResults=40";
          Axios.get(href)
          .then(res=> setBookData(res.data.items)); 
          setIsLoading(false);
        }
        console.log(logData);
      });
    } else {
        navigate("/login");
    }
  }, []);

  if(isLoading) {
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='mylog-page'>
      {booksLogged ? <h2 id='no-logs'>Logs</h2> : <h2 id='no-logs'>No books logged</h2>}
      <div className='log-results'>
        {
          bookData.map((book) => {
            let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;
            let bookId = book.id;

            if(cover !== undefined){
              return (
                <img key={book.id} src={cover} alt='Book cover' onClick={()=> {navigate('/bookdetail',{state:{id: bookId}})}}/> 
              )
            }
          }) 
        }
      </div>

      {bookData.filter(book => logData.includes(book.id)).map(filteredBook => (
            <div key={filteredBook.id} className="overlay-inner">
              <img alt='Book cover' src={filteredBook.volumeInfo.imageLinks && filteredBook.volumeInfo.imageLinks.thumbnail}/>
              <div className='details'>
                <h1>{filteredBook.volumeInfo.title} <i id='close' className="fa-regular fa-circle-left" onClick={()=> {navigate('/books')}}></i></h1><br/>
                <h3>Author: {filteredBook.volumeInfo.authors}</h3><br/>
                <h3>Summary:</h3>
                <p>
                  {filteredBook.volumeInfo.description}
                </p>
