import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { useIsAuthenticated } from 'react-auth-kit';
import { useNavigate } from 'react-router-dom';
import { useAuthUser } from 'react-auth-kit';

function Mylog() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [bookId, setBookId] = useState();
  const [showLog, setShowLog] = useState(false);
  const navigate = useNavigate();  

  useEffect(() => {
    if(isAuthenticated()){
      Axios.get('http://localhost:4000/books', {
        usern: authUser().username
      }).then((response) => {
        if(response.data.message){
          document.getElementById("no-logs").innerHTML = response.data.message;
          document.getElementById("no-logs").style.textAlign = "center";
        } else { 
          document.getElementById("no-logs").style.display = "none";
          setShowLog(true);
        }
        console.log(response.data);
      });
    } else {
        navigate("/login");
    }
  }, [isAuthenticated, authUser, navigate]);

  useEffect(() => {
    if(showLog){
      var href = "https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40";
      Axios.get(href)
        .then(res=> setBookData(res.data.items)); 
        setIsLoading(false)
        console.log(bookData);
    }
  }, [bookData, showLog]);

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const removeBook = () => {
    Axios.post('http://localhost:4000/removebook', {
      usern: usern,
      bookId: bookId,
    }).then((response) => {
      if(response.data.message){
        console.log(response.data.message);
      } else {
        document.getElementById("logbtn").innerHTML = 'Log';
        setTimeout(togglePopup('Book Removed'), 5000);
      }
    });
  }

  function on() {
    document.getElementById("overlay").style.display = "block";
    document.body.classList.add('disable-scroll');
  };

  function off() {
    document.getElementById("overlay").style.display = "none";
    document.body.classList.remove('disable-scroll');
  };

  if(isLoading) {
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='book-page'>
      <h2 id='no-logs'>Logs</h2>
      <div className='book-results'>
        {
          bookData.map((book) => {
            let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;
            let bookId = book.id;

            if(cover !== undefined){
              return (
                <img key={book.id} src={cover} alt='Book cover' onClick={()=> {navigate('/bookdetail',{state:{id: bookId}})}}/> 
              )
            }
          }) 
        }
      </div>


    </div>
  )
}

export default Mylog
