import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { Link } from 'react-router-dom';
import { useIsAuthenticated } from 'react-auth-kit';
import { useAuthUser } from 'react-auth-kit';
import { useLocation } from 'react-router-dom';
import './Styles/BookDetail.css';

function Books() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const [bookData, setBookData] = useState([]);
  const [logStatus, setLogStatus] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const [imgLoaded, setImgLoaded] = useState(false);
  const [logBookData, setLogBookData] = useState([]);
  const [passedLogBook, setPassedLogBook] = useState(false);
  const location = useLocation();
  const bookId = "tqYZ96o4uaEC";
  const pageCall = location.state.page;

  useEffect(() => {
    setBookData(location.state.data);

    if(bookId !== undefined) {
      Axios.get("https://www.googleapis.com/books/v1/volumes?q="+bookId)
      .then(res=> setLogBookData(res.data.items))
      .catch(err=>console.log(err));

      setPassedLogBook(true);
    } else {
      setPassedLogBook(false);
    }

    console.log(bookId,logBookData);
    if(isAuthenticated()) {
        const email = authUser().email;
        Axios.post('http://localhost:4000/booklogged', {
        email: email,
        bookId: bookId
        }).then((response) => {
            if(response.data.message){
                setLogStatus(false);
            } else {
                setLogStatus(true);
            }
        })
        .catch(err=>console.log(err));

    } else {
        setLogStatus(false);
    }

    setTimeout(()=> {
        setIsLoading(false);
    }, 2000);
  }, [])

  function togglePopup(text) {
    document.getElementById("popup").innerHTML = text;

    setTimeout(()=> {
        $("#popup").fadeIn(1000);   
        $("#popup").fadeOut(1000);
    },50) 
  }

  const editLog = () => {
    if(isAuthenticated()) {
        const email = authUser().email;   

        Axios.post('http://localhost:4000/editlog', {
            email: email,
            bookId: bookId,
        }).then((response) => {
            if(response.data.message){
                console.log(response.data.message);
            } else {
                if(response.data.message1) {
                    setLogStatus(false);
                    togglePopup("&emsp;"+response.data.message1);
                } else if(response.data.message2) {
                    setLogStatus(true);
                    togglePopup("&emsp;"+response.data.message2);
                }
            }
        })
        .catch(err=>console.log(err));;
    } else {
        togglePopup('&ensp;Login to log');
    }
  }

  function handleImageLoaded() {
    setImgLoaded(true);
  }

  return (
    <div className='book-page'>
      { isLoading ? <div id='loading'><h1>Grabbing book details...</h1><br/><img  src={require('../assets/images/loading.gif')} alt='loading gif' /> </div> : 
        <>
          {/* { !passedLogBook ?
            <div key={bookData.id} className="book-content">
                { !imgLoaded &&
                    <div className='placeholder'></div>
                }
                <img src={"https://books.google.com/books/publisher/content/images/frontcover/"+bookData.id+"?fife=w800-h1000&source=gbs_api"} alt='Book cover' onLoad={()=> {handleImageLoaded()}}/>
                <Link to={pageCall}><i id='close' className="fa-regular fa-circle-left"></i></Link>

                <h1>{bookData.volumeInfo.title} </h1><br/>
                <h3>Author: {bookData.volumeInfo.authors}</h3><br/>

                <Link id='preview' to={bookData.volumeInfo.previewLink} target="_blank">Preview</Link><br/><br/><br/>
                <button id='logbtn' onClick={()=> {editLog()}}>{logStatus ? <i className="fa-solid fa-check"></i>: <i class="fa-solid fa-plus"></i>}<span id='popup'></span></button>

                <h4>Summary:</h4><br/>
                <p>
                  {bookData.volumeInfo.description}
                </p>  
            </div> 
            :
            <div key={logBookData.id} className="book-content">
                { !imgLoaded &&
                    <div className='placeholder'></div>
                }
                <img src={"https://books.google.com/books/publisher/content/images/frontcover/"+logBookData.id+"?fife=w800-h1000&source=gbs_api"} alt='Book cover' onLoad={()=> {handleImageLoaded()}}/>
                <Link to={pageCall}><i id='close' className="fa-regular fa-circle-left"></i></Link>

                <h1>{logBookData.volumeInfo.title} </h1><br/>
                <h3>Author: {logBookData.volumeInfo.authors}</h3><br/>

                <Link id='preview' to={logBookData.volumeInfo.previewLink} target="_blank">Preview</Link><br/><br/><br/>
                <button id='logbtn' onClick={()=> {editLog()}}>{logStatus ? <i className="fa-solid fa-check"></i>: <i class="fa-solid fa-plus"></i>}<span id='popup'></span></button>

                <h4>Summary:</h4><br/>
                <p>
                  {logBookData.volumeInfo.description}
                </p>  
            </div>
          }  */}
        </>
      }
    </div>     
  )
}

export default Books
