import React from 'react';
import { Link } from 'react-router-dom';
import './signup.css';

function Signup() {

  const [email, setEmail] = useState();
  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();

  const checkInfo = () => {
    const emailMatch = usersData.some(element => element.email === email);
    const usernameMatch = usersData.some(element => element.username === usern);

    if(!emailMatch && !usernameMatch){
      Axios.post('http://localhost:4000/api/insertuser', {email: email, usern: usern, pwd: pwd});

      setSuccess(true);
    } else {
      if(!emailMatch && usernameMatch){
        alert("username in use");
      } else if (emailMatch && !usernameMatch){
        alert("email in use");
      } else {
        alert("email and username in use");
      }
    }
  }

  return (
    <>
    <h1>Welcome to BookQuest</h1>
    <h4>Sign up to start your Journey</h4>
    <div className='signup-form'>
      <form onSubmit={checkInfo}>
        <label>Email</label><br/>
        <input type="email" name="email" required pattern="[a-zA-Z0-9.-_]{1,}@[a-zA-Z.-]{2,}[.]{1}[a-zA-Z]{2,}"/><br/>
        <label>Username [4-15 characters]</label><br/>
        <input type="text" name="username" required minLength="4" maxLength="15"/><br/>
        <label>Password [6-20 characters]</label><br/>
        <input type="password" name="password" required minLength="8" maxLength="20"/><br/>
        <label>Confirm Password</label><br/>
        <input type="password" name="password" required minLength="8" maxLength="20"/><br/>
        <h5>Already have an account? <Link to='/login'>Login Here</Link></h5>
        <input type="submit" name="submit" />
      </form>
    </div>
    </>
  )
}

export default Signup
