import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSignOut } from 'react-auth-kit';
import { useNavigate } from 'react-router-dom';
import { useAuthUser } from 'react-auth-kit';
import './Navbar.css';

function Navbar() {

    const auth = useAuthUser();
    const [loggedIn, setLoggedIn] = useState(false);
    const signOut = useSignOut();
    const navigate = useNavigate();

    const logout = () => {
        signOut();
        setLoggedIn(false);
        navigate("/login");
    } 

    useEffect(() => {
        if(auth()){
            setLoggedIn(true);
        } else {
            setLoggedIn(false);
        }
    })

    return (
        <nav className='navbar'>
            <Link to='/' className='logo'>
                <i className="fa-solid fa-book-open"></i> BookQuest
            </Link>

            <div id='book-popup'>Book Logged</div>

            <div className='navbar-links'>
                <ul>
                    <li>
                        <Link className='navbutton' to='/books'>
                            Books
                        </Link>
                    </li>
                    {loggedIn && (
                        <li className='logged-in'>
                            <Link className='navbutton' to='/mylog'>
                                My Log
                            </Link>
                        </li>
                    )}
                    {!loggedIn && (
                        <>
                        <li>
                            <Link className='navbutton' to='/login'>
                                Login
                            </Link>
                        </li>
                        <li>
                            <Link className='navbutton' to='/signup' id='signup-button'>
                                Sign up
                            </Link>
                        </li>
                        </>
                    )}
                    {loggedIn && (
                        <>
                        <li>
                            <Link to='/Profile' >
                                <img id='user-icon' src={require('../assets/images/user-icon.png')} alt='user-icon' />
                            </Link>

                        </li>
                        <li>
                            <Link className='logoutbtn' to='/' onClick={logout}>
                                <i className="fa-solid fa-right-from-bracket"></i>
                            </Link>
                        </li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    )
}

export default Navbar
