import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { Link } from 'react-router-dom';
import {useLocation} from 'react-router-dom';
import './Styles/Books.css';

function Books() {

  const [searchKey, setSearchKey] = useState("");
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const searchDetail = location.state.search;

  useEffect(() => {
    if(searchDetail === ""){
      var href = "https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE"+"&maxResults=40";
      Axios.get(href)
      .then(res=> setBookData(res.data.items)); 
      setIsLoading(false)
    } else {
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err))
    }

  }, []);

  const searchBook = () => {
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err))
  }

  if(isLoading){
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='book-page'>
      <div className='bar'>
        <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}/>
        <button onClick={()=> {searchBook()}}>Search</button>
      </div>

      <div className='book-results'>
        {
          bookData.map((book) => {
            let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;
            let bookId = book.id;

            if(cover !== undefined){
              return (
                <Link to='/bookdetail' state={{id: bookId, page: '/books', search: searchKey}}>
                  <img key={book.id} src={cover} alt='Book cover' />
                </Link>  
              )
            }
          }) 
        }
      </div>


    </div>
  )
}

export default Books
