import React, { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './login.css';

function Login() {

  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();
  const [loginStatus, setLoginStatus] = useState();

  const checkInfo = () => {
    if((email != "" && email != null && email_format.test(email)) && (usern !== "" && usern !== null && user_format.test(usern)) && (pwd !== "" && pwd !== null && pwd_format.test(pwd)) && confirmPwd !== pwd){
      Axios.post('http://localhost:4000/login', {
        usern: usern,
        pwd: pwd,
      }).then((response) => {
        if(response.data.message3){
          document.getElementById("login-error").style.color = "red";
          setLoginStatus(response.data.message3);
        } else {
          window.location.href = "/";
        }

        console.log(response);
      });
    } else {
      document.getElementById("login-error").style.color = "red";
      setSignupStatus('Please properly fill in the form!');
    }
  }

  return (
    <>
    <h1>Welcome Back</h1>
    <h4 id='login-error'>{loginStatus}</h4>
    <div className='login-form'>
      <form>
        <label>Username</label><br/>
        <input type="text" name="username" onChange={(e => setUsername(e.target.value))} required /><br/>
        <label>Password</label><br/>
        <input type="password" name="password" onChange={(e => setPwd(e.target.value))} required /><br/>
        <h5>Don't have an account? <Link to='/signup'>Sign Up Here</Link></h5>
        <button type="button" onClick={checkInfo}>Login</button>
      </form>
    </div>
    </>
  )
}

export default Login
