import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Axios from 'axios';
import $ from 'jquery';
import './book_detail.css';

function Book_detail() {

  const [bookInfo, setBookInfo] = useState();
  const location = useLocation();

  setBookInfo = location.state.book;



  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
    if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
      document.getElementById("logbtn").innerHTML = '+';
      togglePopup('Book Removed');
    }
    else {
      document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
      togglePopup('Book Logged');
    }
  }

  return (
    <div className='book-info'>
      {
        <>
          <div key={bookInfo.id}>
            <img alt='Book cover' src={bookInfo.volumeInfo.imageLinks&&bookInfo.volumeInfo.imageLinks.smallThumbnail}/>
            <div className='details'>
              <h1>{bookInfo.volumeInfo.title} <button id='logbtn' onClick={logBook}>+</button></h1>
              <h3>Author: {bookInfo.volumeInfo.authors}</h3><br/>
              <p>
                {bookInfo.volumeInfo.description}
              </p>
              <Link to='/books'>
                <button>Back</button>
              </Link>
            </div>
          </div>
        </>    
      }
    </div>
  )
}

export default Book_detail
