import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Axios from 'axios';
import $ from 'jquery';
import './book_detail.css';

function Book_detail() {

  const [bookInfo, setBookInfo] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q='+location.state.id+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE')
      .then(res=> console.log(res.data.items[0])); 
      setIsLoading(false)
  }, [])

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
    if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
      document.getElementById("logbtn").innerHTML = '+';
      togglePopup('Book Removed');
    }
    else {
      document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
      togglePopup('Book Logged');
    }
  }

  if(isLoading){
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='book-info'>
      {
        <>

        </>    
      }
    </div>
  )
}

export default Book_detail
