import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Axios from 'axios';
import $ from 'jquery';
import './book_detail.css';

function Book_detail() {

  const [bookInfo, setBookInfo] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q='+location.state.id+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE')
      .then(res=> setBookInfo(res.data.items)); 
      setIsLoading(false)
  }, [])

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
    if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
      document.getElementById("logbtn").innerHTML = '+';
      togglePopup('Book Removed');
    }
    else {
      document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
      togglePopup('Book Logged');
    }
  }

  if(isLoading){
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='book-info'>
      {
        bookInfo.filter(book => book.id === location.state.id).map((filteredbook) => {
          let cover = filteredbook.volumeInfo.imageLinks && filteredbook.volumeInfo.imageLinks.smallThumbnail;
          let bookTitle = filteredbook.volumeInfo.title;
          let bookAuthor = filteredbook.volumeInfo.authors;
          let bookSummary = filteredbook.volumeInfo.description;

          return (
            <div key={book.id}>
              <img alt='Book cover' src={cover}/>
              <div className='details'>
                <h1>{bookTitle} <button id='logbtn' onClick={logBook}>+</button></h1>
                <h3>Author: {bookAuthor}</h3><br/>
                <p>
                  {bookSummary}
                </p>
                <Link to='/books'>
                  <button>Back</button>
                </Link>
              </div>
            </div>
          )
        })    
      }
    </div>
  )
}

export default Book_detail
