import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { useIsAuthenticated } from 'react-auth-kit';
import { useNavigate } from 'react-router-dom';
import { useAuthUser } from 'react-auth-kit';
import './Styles/Books.css';

function Books() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const [searchKey, setSearchKey] = useState("");
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [bookId, setBookId] = useState();

  const searchBook = (e) => {
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err))
  }

  useEffect(() => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items)); 
      setIsLoading(false)
  }, [])

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(5000);
  }

  const logBook = () => {
    if(isAuthenticated()) {
      if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
        Axios.post('http://localhost:4000/logbook', {
          usern: authUser().username,
          bookId: bookId,
        }).then((response) => {
          if(response.data.message){
            console.log(response.data.message);
          } else {
            document.getElementById("logbtn").innerHTML = 'Log';
            setTimeout(togglePopup('Book Removed'), 5000);
          }
        });
      } else {
        Axios.post('http://localhost:4000/removebook', {
          usern: authUser().username,
          bookId: bookId,
        }).then((response) => {
          if(response.data.message){
            console.log(response.data.message);
          } else {
            document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
            togglePopup('Book Logged');
          }
        });
      }
    } else {
      togglePopup('Log in to log books');
    }
  }

  function on(element) {
    document.getElementById("overlay").style.display = "block";
    document.body.classList.add('disable-scroll');
  };

  function off() {
    document.getElementById("overlay").style.display = "none";
    document.body.classList.remove('disable-scroll');
  };

  if(isLoading){
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='book-page'>
      <div className='bar'>
        <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}/>
        <button onClick={searchBook}>Search</button>
      </div>

      <div className='book-results'>
        {
          bookData.map((book) => {
            let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;

            if(cover !== undefined){
              return (
                <img key={book.id} src={cover} alt='Book cover' onClick={()=> {on(); setBookId(book.id)}} /> 
              )
            }
          }) 
        }
      </div>


    </div>
  )
}

export default Books
