import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { Link } from 'react-router-dom';
import $ from 'jquery';
import './Styles/Books.css';

function Books() {
  const [searchKey, setSearchKey] = useState();
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const searchT = localStorage.getItem("searchKey");

  useEffect(() => {
    if(searchT === undefined) {
      console.log("no search");
      Axios.get("https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE"+"&maxResults=40")
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err));
    } else {
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err));
    }

    setTimeout(()=> {
      setIsLoading(false);
    }, 1000);
  }, []);

  const searchBook = () => {
    if(searchKey !== undefined) {
      setIsLoading(true);
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err));

      localStorage.setItem("searchKey", searchKey);

      setTimeout(()=> {
        setIsLoading(false);
      }, 1000);
    }

  }  

  return (
    <>
    <div id='search-bar'>
      <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}  />
      <i className="fa-solid fa-magnifying-glass" onClick={()=> {searchBook()}}></i>
    </div>

    <div className='book-page'>
      { isLoading ? <div><br/><br/><h1>Grabbing books...</h1><img id='loading' src={require('../assets/images/loading.gif')} alt='loading gif' /> </div> : 
        <div className='book-results'>
          { 
            bookData.map((book) => {
              let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;
              let bookId = book.id;

              if(cover !== undefined){
                return (
                  <Link key={book.id} to='/bookdetail' state={{id: bookId, page: '/books', term: searchKey}}>
                    <img src={cover} alt='Book cover' />
                  </Link>  
                )
              }
            }) 
          }
        </div>
      }
    </div>
    </>
  )
}

export default Books
