import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './signup.css';

function Signup() {

  const email_format = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
  const user_format = /^[a-zA-Z0-9]{3,15}$/;
  const pwd_format = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*_-])(?=.{8,20})/;

  const [usersData, setUsersData] = useState([]);
  const [email, setEmail] = useState();
  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();
  const [confirmPwd, setConfirmPwd] = useState();

  const [focusEmail, setFocusEmail] = useState();
  const [focusUsern, setFocusUsern] = useState();
  const [focusPwd, setFocusPwd] = useState();
  const [matchPwd, setMatchPwd] = useState();

  useEffect(() => {
    Axios.get('http://localhost:4000/api/users').then(response => {
      setUsersData(response.data);
    });
  }, []); 

  const checkInfo = async (e) => {
    e.preventDefault();

    const emailMatch = usersData.some(element => element.email === email);
    const usernameMatch = usersData.some(element => element.username === usern);

    if(email != null && usern != null && !emailMatch && !usernameMatch){
      Axios.post('http://localhost:4000/api/insertuser', {email: email, usern: usern, pwd: pwd});
      window.location.href = "/login";
    }
  }

   useEffect(() => {
    const emailMatch = usersData.some(element => element.email === email);
    const usernameMatch = usersData.some(element => element.username === usern);

    if (email_format.test(email) && emailMatch){
      document.getElementById("email-error").style.display = "inline";
    } else {
      document.getElementById("email-error").style.display = "none";
    } 

    if (user_format.test(usern) && usernameMatch){
      document.getElementById("username-error").style.display = "inline";
    } else {
      document.getElementById("username-error").style.display = "none";
    } 

    if (pwd_format.test(pwd)){
      document.getElementById("pass-error").style.display = "inline";
    } else {
      document.getElementById("pass-error").style.display = "none";
    } 

    if (matchPwd && confirmPwd != pwd){
      document.getElementById("mpass-error").style.display = "inline";
    } else {
      document.getElementById("mpass-error").style.display = "none";
    } 

  });

  return (
    <>
    <h1>Welcome to BookQuest</h1>
    <h4>Sign up to start your Journey</h4>
    <div className='signup-form'>
      <form onSubmit={checkInfo}>
        <label>Email <span id='email-error'>already in use</span></label><br/>
        <input type="email" name="email" onChange={(e => setEmail(e.target.value))} /><br/>
        <label>Username <span id='username-error'>already in use</span></label><br/>
        <input type="text" name="username" onChange={(e => setUsername(e.target.value))} /><br/>
        <label>Password <span id='pass-error'></span></label><br/>
        <input type="password" name="password" onChange={(e => setPwd(e.target.value))} /><br/>
        <label>Confirm Password <span id='mpass-error'>Must match</span></label><br/>
        <input type="password" name="password" onFocus={() => {setMatchPwd(true)}} onChange={(e => setConfirmPwd(e.target.value))} /><br/>
        <h5>Already have an account? <Link to='/login'>Login Here</Link></h5>
        <button disabled="true">Submit</button>
      </form>
    </div>
    </>
  )
}

export default Signup
