import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { Link } from 'react-router-dom';
import { useIsAuthenticated } from 'react-auth-kit';
import { useAuthUser } from 'react-auth-kit';
import {useLocation} from 'react-router-dom';
import './Styles/BookDetail.css';

function Books() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const [bookData, setBookData] = useState([]);
  const location = useLocation();
  const bookId = location.state.id;
  const pageCall = location.state.page;
  const [logStatus, setLogStatus] = useState();

  useEffect(() => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items)); 

    if(isAuthenticated()) {
        const usern = authUser().username;
        Axios.post('http://localhost:4000/booklogged', {
        usern: usern,
        bookId: bookId
        }).then((response) => {
            if(response.data.message){
                console.log(response.data.message);
                setLogStatus(false);
            } else {
                setLogStatus(true);
            }
        });
    } else {
        setLogStatus(false);
    }
  }, [])

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(5000);
  }

  const logBook = () => {
    if(isAuthenticated()) {
      const usern = authUser().username;
      if(logStatus === '<i class="fa-solid fa-check"></i>'){
        Axios.post('http://localhost:4000/removebook', {
          usern: usern,
          bookId: bookId,
        }).then((response) => {
          if(response.data.message){
            console.log(response.data.message);
          } else {
            setLogStatus(false);
            setTimeout(togglePopup('Book Removed'), 5000);
          }
        });
      } else {
        Axios.post('http://localhost:4000/logbook', {
          usern: usern,
          bookId: bookId,
        }).then((response) => {
          if(response.data.message){
            console.log(response.data.message);
          } else {
            setLogStatus(true);
            togglePopup('Book Logged');
          }
        });
      }
    } else {
      togglePopup('Log in to log books');
    }
  }

  return (
    <div className='book-page'>
          {bookData.filter(book => book.id === bookId).map(filteredBook => (
            <div key={filteredBook.id} className="overlay-inner">
              <img alt='Book cover' src={filteredBook.volumeInfo.imageLinks && filteredBook.volumeInfo.imageLinks.thumbnail}/>
              <div className='details'>
                <h1>{filteredBook.volumeInfo.title} <Link to={pageCall} state={{search: location.state.search}} ><i id='close' className="fa-regular fa-circle-left"></i></Link></h1><br/>
                <h3>Author: {filteredBook.volumeInfo.authors}</h3><br/>
                <h3>Summary:</h3>
                <p>
                  {filteredBook.volumeInfo.description}
                </p>
                <button id='logbtn' onClick={logBook}>{logStatus ? <i className="fa-solid fa-check"></i> : 'Log'}</button>&ensp;
                <div id='book-popup'>Book Logged</div>
              </div>
            </div>
          ))} 
      </div>     
  )
}

export default Books
