import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './books.css';

function Books() {

  const [searchKey, setSearchKey] = useState("");
  const [bookData, setBookData] = useState([]);

  const searchBook = (e) => {
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err))
  }

  return (
    <div className='book-page'>
      <div className='bar'>
        <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}/>
        <button onClick={searchBook}>Search</button>
      </div>

        <div className='book-results'>
          {
            bookData.map((book) => {
              return (
                <Link to='/book_detail' state={{}}>
                  <img alt='Book cover' src={book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumnail}/>
                </Link>
              )
            }) 
          }
        </div>
    </div>
  )
}
{/*state={{title: filteredBook.name}}*/}
export default Books
