import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './Profile.css';

function Profile() {

  const email_format = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
  const user_format = /^[a-zA-Z0-9]{3,15}$/;
  const pwd_format = /^[a-zA-Z0-9]{8,20}$/;

  const [email, setEmail] = useState();
  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();
  const [confirmPwd, setConfirmPwd] = useState();

  const [focusEmail, setFocusEmail] = useState();
  const [focusUsern, setFocusUsern] = useState();
  const [focusPwd, setFocusPwd] = useState();
  const [matchPwd, setMatchPwd] = useState();
  const [signupStatus, setSignupStatus] = useState();

  const updateInfo = () => {
    document.getElementsByName("email").disabled = "false";
    document.getElementsByName("username").disabled = "false";
    document.getElementsByName("password").disabled = "false";
    document.getElementsByName("password-match").disabled = "false";
    console.log("hola");
  }

  const signUser = () => {
    if((email != "" && email != null && email_format.test(email)) && (usern !== "" && usern !== null && user_format.test(usern)) && (pwd !== "" && pwd !== null && pwd_format.test(pwd) && confirmPwd === pwd)){
      Axios.post('http://localhost:4000/register', {
        email: email,
        usern: usern,
        pwd: pwd,
      }).then((response) => {
        if(response.data.message){
          document.getElementById("signup-error").style.color = "red";
          setSignupStatus(response.data.message);
        } else {
          console.log(response.data.message);
          window.location.href = "/login";
        }

        console.log(response);
      });
    } else {
      document.getElementById("signup-error").style.color = "red";
      setSignupStatus('Please fill in the form to register!');
    }
  }

  return (
    <div className='profile-form'>
      <img className='profile-icon' src={require('../assets/images/user-icon.png')} alt='user-icon' /><br/>
      <h1>Profile Information</h1><br/><br/>
      <form>
        <label>Email <span id='email-error'>[invalid email]</span></label><br/>
        <input type="text" name="email" onFocus={() => {setFocusEmail(true)}} onBlur={() => {setFocusEmail(false)}} onChange={(e => setEmail(e.target.value))} disabled/><br/>
        <label>Username <span id='username-error'>[3-15 characters]</span></label><br/>
        <input type="text" name="username" onFocus={() => {setFocusUsern(true)}} onBlur={() => {setFocusUsern(false)}} onChange={(e => setUsername(e.target.value))} disabled/><br/>
        <label>Password <span id='pass-error'>[8-20 characters]</span></label><br/>
        <input type="password" name="password" onFocus={() => {setFocusPwd(true)}} onBlur={() => {setFocusPwd(false)}} onChange={(e => setPwd(e.target.value))} disabled/><br/>
        <label>Confirm Password <span id='mpass-error'>[Must match]</span></label><br/>
        <input type="password" name="password-match" onFocus={() => {setMatchPwd(true)}} onBlur={() => {setMatchPwd(false)}} onChange={(e => setConfirmPwd(e.target.value))} disabled/><br/>
        <button type='button' onClick={updateInfo}>Update</button>
      </form>
    </div>
  )
}

export default Profile
