import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSignOut } from 'react-auth-kit';
import { useNavigate } from 'react-router-dom';
import { useIsAuthenticated } from 'react-auth-kit';
import './Styles/Navbar.css';

function Navbar() {

    const isAuthenticated = useIsAuthenticated();
    const [loggedIn, setLoggedIn] = useState(false);
    const signOut = useSignOut();
    const navigate = useNavigate();
    const [searchKey, setSearchKey] = useState();

    const logout = () => {
        signOut();
        setLoggedIn(false);
        navigate("/login");
    } 

    useEffect(() => {
        if(isAuthenticated()){
            setLoggedIn(true);
        } else {
            setLoggedIn(false);
        }

    }, [isAuthenticated]);

    const transition = (page) => {
        if(page === "home" && window.location.pathname === "/books"){
            setTimeout(()=> {
                document.getElementById("navbar").style.paddingBottom = "0px";
            }, 50);
        } else {
            setTimeout(()=> {
                document.getElementById("navbar").style.paddingBottom = "0px";
            }, 50);
        }

    }

    useEffect(()=> {
        if(window.location.pathname === "/books") {
            document.getElementById("navbar").style.paddingBottom = "0px";
        }
    })

    const searchBook = () => {
        // Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
        // .then(res=> setBookData(res.data.items))
        // .catch(err=>console.log(err))
      }

    return (
        <nav id='navbar'>
            <div className='nav-buttons'>
            <Link to='/' className='logo'>
                <i className="fa-solid fa-book-open" onClick={transition('home')}></i> BookQuest
            </Link>

            <div className='navbar-links'>
                <ul>
                    <li onClick={transition('books')}>
                        <Link className='navbutton' to='/books'>
                            Books
                        </Link>
                    </li>
                    {loggedIn && (
                        <li className='logged-in' onClick={transition('home')}>
                            <Link className='navbutton' to='/mylog'>
                                My Log
                            </Link>
                        </li>
                    )}
                    {!loggedIn && (
                        <>
                        <li onClick={transition('home')}>
                            <Link className='navbutton' to='/login'>
                                Login
                            </Link>
                        </li>
                        <li onClick={transition('home')}>
                            <Link className='navbutton' to='/signup' id='signup-button'>
                                Sign up
                            </Link>
                        </li>
                        </>
                    )}
                    {loggedIn && (
                        <>
                        <li onClick={transition('home')}>
                            <Link to='/Profile' >
                                <img id='user-icon' src={require('../assets/images/user-icon.png')} alt='user-icon' />
                            </Link>

                        </li>
                        <li className='logoutbtn' onClick={transition('home')}>
                            <i className="fa-solid fa-right-from-bracket" onClick={logout}></i>
                        </li>
                        </>
                    )}
                </ul>
            </div>
            </div>

            <div id='search-bar'>
                <div id='search-inputs'>
                    <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}/>
                    <button onClick={()=> {searchBook()}}>Search</button>
                </div>
            </div>
        </nav>
    )
}

export default Navbar
