import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import './books.css';
import BookDetail from '../components/BookDetail';

function Books() {

  const [searchKey, setSearchKey] = useState("");
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [show, setShow] = useState(false);
  const [bookItem, setBookItem] = useState();

  const searchBook = (e) => {
      Axios.get('https://www.googleapis.com/books/v1/volumes?q='+searchKey+'&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items))
      .catch(err=>console.log(err))
  }

  useEffect(() => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=free-ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items)); 
      setIsLoading(false)
  }, [])

  if(isLoading){
    return(
      <div className='loading'>Loading...</div>
    )
  }

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
    if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
      document.getElementById("logbtn").innerHTML = '+';
      togglePopup('Book Removed');
    }
    else {
      document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
      togglePopup('Book Logged');
    }
  }

  return (
    <div className='book-page'>
      <div className='bar'>
        <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}/>
        <button onClick={searchBook}>Search</button>
      </div>

        <div className='book-results'>
          {
            bookData.map((book) => {
              let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.smallThumbnail;

              if(cover !== undefined){
                return (
                  <>
                    <img src={cover} alt='Book cover' onClick={()=>{ setShow(true); setBookItem(book) }} />

                    <div className="overlay">
                      <div className="overlay-inner">
                        <button className="close" onClick={onClose}><i class="fa-solid fa-circle-xmark"></i></button>
                        <div className="inner-box">
                          <img alt='Book cover' src={cover}/>
                          <div className='details'>
                            <h1>{book.volumeInfo.title} <button id='logbtn' onClick={logBook}>+</button></h1>
                            <h3>Author: {book.volumeInfo.authors}</h3><br/>
                            <p>
                              {book.volumeInfo.description}
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>

                  </>
                )
              }
            }) 
          }
        </div>
    </div>
  )
}

export default Books
