import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { Link } from 'react-router-dom';
import { useIsAuthenticated } from 'react-auth-kit';
import { useAuthUser } from 'react-auth-kit';
import { useLocation } from 'react-router-dom';
import './Styles/BookDetail.css';

function Books() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const [bookData, setBookData] = useState([]);
  const [logStatus, setLogStatus] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();
  const bookId = location.state.id;
  const pageCall = location.state.page;

  useEffect(() => {
    setBookData(location.state.data);

    if(isAuthenticated()) {
        const email = authUser().email;
        Axios.post('http://localhost:4000/booklogged', {
        email: email,
        bookId: bookId
        }).then((response) => {
            if(response.data.message){
                setLogStatus(false);
            } else {
                setLogStatus(true);
            }
        })
        .catch(err=>console.log(err));

    } else {
        setLogStatus(false);
    }

    setTimeout(()=> {
        setIsLoading(false);
      }, 1500);
  }, [])

  function togglePopup(text) {
    document.getElementById("popup").innerHTML = text;

    setTimeout(()=> {
        const element = document.querySelector('#logbtn');
        element.style.setProperty('width', '400px');


    }, 50);
    $("#popup").fadeIn(1000);   
    $("#popup").fadeOut(1000);    
  }

  const editLog = () => {
    if(isAuthenticated()) {
        const email = authUser().email;   

        Axios.post('http://localhost:4000/editlog', {
            email: email,
            bookId: bookId,
        }).then((response) => {
            if(response.data.message){
                console.log(response.data.message);
            } else {
                if(response.data.message1) {
                    setLogStatus(false);
                    togglePopup(response.data.message1);
                } else if(response.data.message2) {
                    setLogStatus(true);
                    togglePopup(response.data.message2);
                }
            }
        })
        .catch(err=>console.log(err));;
    } else {
        togglePopup('Login to log');
    }
  }

  return (
    <div className='book-page'>
      { isLoading ? <div><br/><br/><h1>Grabbing book details...</h1><img id='loading' src={require('../assets/images/loading.gif')} alt='loading gif' /> </div> : 
        <>
        {bookData.filter(book => book.id === bookId).map(filteredBook => (
            <div key={filteredBook.id} className="book-content">
                <img alt='Book cover' src={"https://books.google.com/books/publisher/content/images/frontcover/"+filteredBook.id+"?fife=w400-h600&source=gbs_api"}/>

                <Link to={pageCall}><i id='close' className="fa-regular fa-circle-left"></i></Link>

                <h1>{filteredBook.volumeInfo.title} </h1><br/>
                <h3>Author: {filteredBook.volumeInfo.authors}</h3><br/>

                <Link id='preview' to={filteredBook.volumeInfo.previewLink} target="_blank">Preview</Link><br/><br/><br/>
                <button id='logbtn' onClick={()=> {editLog()}}>{logStatus ? <i className="fa-solid fa-check"></i>: 'Log'}<div id='popup'>Book Logged</div></button>

                <h4>Summary:</h4><br/>
                <p>
                  {filteredBook.volumeInfo.description}
                </p>  
            </div>
          ))} 
          </>
      }
      </div>     
  )
}

export default Books
