import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './signup.css';

function Signup() {

  const [usersData, setUsersData] = useState([]);
  const [email, setEmail] = useState();
  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();
  const [confirmPwd, setConfirmPwd] = useState();

  useEffect(() => {
    Axios.get('http://localhost:4000/api/users').then(response => {
      setUsersData(response.data);
    });
  }, []); 

  const checkInfo = () => {
    const emailMatch = usersData.some(element => element.email === email);
    const usernameMatch = usersData.some(element => element.username === usern);

    if(email != null && usern != null && !emailMatch && !usernameMatch){
      Axios.post('http://localhost:4000/api/insertuser', {email: email, usern: usern, pwd: pwd});
      window.location.href = "/login";
    }
  }

   useEffect(() => {
    const emailMatch = usersData.some(element => element.email === email);
    const usernameMatch = usersData.some(element => element.username === usern);

    if (emailMatch){
      document.getElementById("email-error").style.display = "inline";
    } else {
      document.getElementById("email-error").style.display = "none";
    } 

    if (usernameMatch){
      document.getElementById("username-error").style.display = "inline";
    } else {
      document.getElementById("username-error").style.display = "none";
    } 

  });

  return (
    <>
    <h1>Welcome to BookQuest</h1>
    <h4>Sign up to start your Journey</h4>
    <div className='signup-form'>
      <form onSubmit={checkInfo}>
        <label>Email <span id='email-error'>already in use</span></label><br/>
        <input type="email" name="email" onChange={(e => setEmail(e.target.value))} required pattern="[a-zA-Z0-9.-_]{1,}@[a-zA-Z.-]{2,}[.]{1}[a-zA-Z]{2,}"/><br/>
        <label>Username <span id='username-error'>already in use</span></label><br/>
        <input type="text" name="username" onChange={(e => setUsername(e.target.value))} required minLength="3" maxLength="15"/><br/>
        <label>Password</label><br/>
        <input type="password" name="password" onChange={(e => setPwd(e.target.value))} required minLength="8" maxLength="20"/><br/>
        <label>Confirm Password</label><br/>
        <input type="password" name="password" required minLength="8" maxLength="20"/><br/>
        <h5>Already have an account? <Link to='/login'>Login Here</Link></h5>
        <button type="submit" onClick={checkInfo} >Submit</button>
      </form>
    </div>
    </>
  )
}

export default Signup
