import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';
import { useIsAuthenticated } from 'react-auth-kit';
import { Link, useNavigate } from 'react-router-dom';
import { useAuthUser } from 'react-auth-kit';
import './Styles/MyLog.css';

function MyLog() {

  const isAuthenticated = useIsAuthenticated();
  const authUser = useAuthUser();
  const navigate = useNavigate();  
  const [bookData, setBookData] = useState([]);
  const [logData, setLogData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [booksLogged, setBooksLogged] = useState();
  let list;

  useEffect(() => {
    if(isAuthenticated()){
      const usern = authUser().username;
      Axios.post('http://localhost:4000/books', {
        usern: usern
      }).then((response) => {
        if(response.data.message){
          setBooksLogged(false);
          setIsLoading(false);
        } else { 
          setBooksLogged(true);
          setIsLoading(false);
          setLogData(response.data.map(log => { return log.book_id; }));

          var href = "https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE"+"&maxResults=40";
          Axios.get(href)
          .then(res=> setBookData(res.data.items)); 
        }
      });
    } else {
      navigate("/login");
    }
  }, []);

  return (
    <div className='mylog-page'>
      { isLoading ? <div><br/><br/><h1>Just grabbing your books...</h1><img id='loading' src={require('../assets/images/loading.gif')} alt='loading gif' /> </div> :
      <>{booksLogged ? '' : <h2 id='no-logs'>No books logged</h2>}
      <div className='log-results'>
        {
          bookData.filter(book => logData.includes(book.id)).map((book) => {
            let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;
            let bookId = book.id;

            if(cover !== undefined){
              return (
                <Link key={book.id} to='/bookdetail' state={{id: bookId, page: '/mylog'}}>
                  <img src={cover} alt='Book cover' />
                </Link>  
              )
            }
          }) 
        }
      </div>
      </>
      }
    </div>
  )
}

export default MyLog
