import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import $ from 'jquery';
import './book_detail.css';

function Book_detail() {

  const [bookData, setBookData] = useState([]);
  const location = useLocation();

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
    if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
      document.getElementById("logbtn").innerHTML = '+';
      togglePopup('Book Removed');
    }
    else {
      document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
      togglePopup('Book Logged');
    }
  }

  return (
    <>

    <div className='book-info'>
      <img alt='Book cover' src={require('../assets/images/book.jpeg')}/>
        <div className='details'>
          <h1>The Alchemist <button id='logbtn' onClick={logBook}>+</button></h1>
          <h3>Author: Paulo Coelho</h3><br/>
          <p>
            A recurring dream troubles Santiago, a young and adventurous 
            Andalusian shepherd. He has the dream every time he sleeps 
            under a sycamore tree that grows out of the ruins of a church. 
            During the dream, a child tells him to seek treasure at the 
            foot of the Egyptian pyramids. Santiago consults a gypsy woman 
            to interpret the dream, and to his surprise she tells him to 
            go to Egypt. A strange, magical old man named Melchizedek, who 
            claims to be the King of Salem, echoes the gypsyâ€™s advice and 
            tells Santiago that it is his Personal Legend to journey to the 
            pyramids. Melchizedek convinces Santiago to sell his flock and 
            set off to Tangier. When Santiago arrives in Tangier, a thief robs 
            him, forcing him to find work with a local crystal merchant. The 
            conservative and kindly merchant teaches Santiago several lessons, 
            and Santiago encourages the merchant to take risks with his business. 
            The risks pay off, and Santiago becomes a rich man in just a year.
          </p>

          <Link to='/books'>
            <button id='backbtn'>Back</button>
          </Link>
        </div>
      {bookData.filter(book => book.book_id === location.state.title).map(filteredBook => (
        <>
        <img alt='Book cover' src={filteredBook.cover}/>
        <div className='details'>
          <h1>{filteredBook.title} <button id='logbtn' onClick={logBook}>+</button></h1>
          <h3>Author: {filteredBook.author}</h3><br/>
          <p>
            {filteredBook.summary}
          </p>
          <Link to='/books'>
            <button>Back</button>
          </Link>
        </div>
        </>
      ))}
    </div>
    </>
  )
}

export default Book_detail
