import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Axios from 'axios';
import $ from 'jquery';
import './book_detail.css';

function Book_detail() {

  const [bookData, setBookData] = useState([]);
  const location = useLocation();

  useEffect(() => {
    Axios.get('https://www.googleapis.com/books/v1/volumes/'+location.state.title+'?key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE')
      .then(res=> setBookData(res.data))
      .catch(err=>console.log(err))
  }, [])

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
    if(document.getElementById("logbtn").innerHTML === '<i class="fa-solid fa-check"></i>'){
      document.getElementById("logbtn").innerHTML = '+';
      togglePopup('Book Removed');
    }
    else {
      document.getElementById("logbtn").innerHTML = '<i class="fa-solid fa-check"></i>';
      togglePopup('Book Logged');
    }
  }

  return (
    <>

    <div className='book-info'>
      {
        <>
          <div key={bookData.id}>
            <img alt='Book cover' src={bookData.volumeInfo.imageLinks && bookData.volumeInfo.imageLinks.smallThumbnail}/>
            <div className='details'>
              <h1>{bookData.volumeInfo.title} <button id='logbtn' onClick={logBook}>+</button></h1>
              <h3>Author: {bookData.volumeInfo.authors}</h3><br/>
              <p>
                {bookData.volumeInfo.summary}
              </p>
              <Link to='/books'>
                <button>Back</button>
              </Link>
            </div>
          </div>
        </>    
      }
    </div>
    </>
  )
}

export default Book_detail
