import React, { useState, useEffect} from 'react';
import { BrowserRouter as Router, Routes, Route} from "react-router-dom";
import './App.css';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import Books from './pages/Books';
import MyLog from './pages/Mylog';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Profile from './pages/Profile';
import Error from './pages/error';

function App(){

  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();
  const [loginStatus, setLoginStatus] = useState(false);
  const [loggedOut, setLoggedOut] = useState(true);
  const [loggedIn, setLoggedIn] = useState(false);

  const userState = () => {
      setLoggedIn(!loggedIn);
      setLoggedOut(!loggedOut);
  }

  const checkInfo = () => {
    if((usern != "" && usern != null) && (pwd != "" && pwd != null)){
      Axios.post('http://localhost:4000/login', {
        usern: usern,
        pwd: pwd,
      }).then((response) => {
        if(!response.data.auth){
          document.getElementById("login-error").style.color = "red";
          document.getElementById("login-error").innerHTML = response.data.message;
          setLoginStatus(false);
        } else { 
          localStorage.setItem("token", response.data.token);

        }

        userAuthenticated();
      });
    } else {
      document.getElementById("login-error").style.color = "red";
      document.getElementById("login-error").innerHTML = 'Please enter your info to login!';
    }
  }

  const userAuthenticated = () => {
    Axios.get('http://localhost:4000/isUserAuth', {
      headers: {
        "x-access-token": localStorage.getItem("token"),
      }
    }).then((response) => {
      console.log(response);
      if(response === "User Authenticated!") {
        userState;
      }
    })
  }

  return (
    <Router>
      <Navbar />

      <div className='body-container'>
        <Routes>
          <Route path='/' element={<Home />}/>
          <Route path='/books' element={<Books />}/>
          <Route path='/mylog' element={<MyLog />}/>
          <Route path='/login' element={<Login />}/>
          <Route path='/signup' element={<Signup />}/>
          <Route path='/profile' element={<Profile />}/>
          <Route path='*' element={<Error />}/>
        </Routes>
      </div>

      <Footer />
    </Router>
  )
}

export default App;
