import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSignOut } from 'react-auth-kit';
import { useNavigate } from 'react-router-dom';
import { useIsAuthenticated } from 'react-auth-kit';
import $ from 'jquery';
import './Styles/Navbar.css';

function Navbar() {

    const isAuthenticated = useIsAuthenticated();
    const [loggedIn, setLoggedIn] = useState(false);
    const signOut = useSignOut();
    const navigate = useNavigate();
    const [searchKey, setSearchKey] = useState();

    const logout = () => {
        signOut();
        setLoggedIn(false);
        navigate("/login");
    } 

    useEffect(() => {
        if(isAuthenticated()){
            setLoggedIn(true);
        } else {
            setLoggedIn(false);
        }

    }, [isAuthenticated]);

    useEffect(()=> {
        if(window.location.pathname === "/books") {
            setTimeout(()=> {
                document.getElementById("search-bar").style.height = "90px";
                $("#search-inputs").fadeIn(1500);
            }, 50);
        } else {
            setTimeout(()=> {
                document.getElementById("search-bar").style.height = "0px";
                $("#search-inputs").fadeOut(20);
            }, 50);
        }

        setTimeout(()=> {
            localStorage.setItem("searchKey", "");
        }, 1 * 60 * 1000);
    })

    return (
        <nav id='navbar'>
            <div className='nav-buttons'>
                <Link to='/' className='logo'>
                    <i className="fa-solid fa-book-open"></i> BookQuest
                </Link>

                <div className='navbar-links'>
                    <ul>
                        <li>
                            <Link className='navbutton' to='/books'>
                                Books
                            </Link>
                        </li>
                        {loggedIn && (
                            <li>
                                <Link className='navbutton' to='/mylog'>
                                    My Log
                                </Link>
                            </li>
                        )}
                        {!loggedIn && (
                            <>
                            <li>
                                <Link className='navbutton' to='/login'>
                                    Login
                                </Link>
                            </li>
                            <li>
                                <Link className='navbutton' to='/signup' id='signup-button'>
                                    Sign up
                                </Link>
                            </li>
                            </>
                        )}
                        {loggedIn && (
                            <>
                            <li>
                                <Link to='/Profile' >
                                    <img id='user-icon' src={require('../assets/images/user-icon.png')} alt='user-icon' />
                                </Link>

                            </li>
                            <li className='logoutbtn'>
                                <i className="fa-solid fa-right-from-bracket" onClick={logout}></i>
                            </li>
                            </>
                        )}
                    </ul>
                </div>
            </div>

            <div id='search-bar'>
                <div id='search-inputs'>
                    <input type="text" placeholder="Title, author, or genre" value={searchKey} onChange={e => setSearchKey(e.target.value)}/>
                    <button onClick={()=> {localStorage.setItem("searchKey", searchKey)}}>Search</button>
                </div>
            </div>
        </nav>
    )
}

export default Navbar
