import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './Navbar.css';

function Navbar() {

    const [loggedOut, setLoggedOut] = useState(true);
    const [loggedIn, setLoggedIn] = useState(false);

    const userState = () => {
        userAuthenticated();
    }

    const userAuthenticated = () => {
        Axios.get('http://localhost:4000/isUserAuth', {
          headers: {
            "x-access-token": localStorage.getItem("token"),
          }
        }).then((response) => {
          console.log(response);
          if(response === "User Authenticated!") {
            setLoggedIn(!loggedIn);
            setLoggedOut(!loggedOut);
          }
        })
      }

    useEffect(() => {
        userAuthenticated;
    }, []) 

    return (
        <nav className='navbar'>
            <Link to='/' className='logo'>
                <i className="fa-solid fa-book-open"></i> BookQuest
            </Link>

            <div id='book-popup'>Book Logged</div>

            <div className='navbar-links'>
                <ul>
                    <li>
                        <Link className='navbutton' to='/books'>
                            Books
                        </Link>
                    </li>
                    {loggedIn && (
                        <li className='logged-in'>
                            <Link className='navbutton' to='/mylog'>
                                My Log
                            </Link>
                        </li>
                    )}
                    {loggedOut && (
                        <>
                        <li>
                            <Link className='navbutton' to='/login'>
                                Login
                            </Link>
                        </li>
                        <li>
                            <Link className='navbutton' to='/signup' id='signup-button'>
                                Sign up
                            </Link>
                        </li>
                        </>
                    )}
                    {loggedIn && (
                        <>
                        <li>
                            <Link to='/Profile' >
                                <img id='user-icon' src={require('../assets/images/user-icon.png')} alt='user-icon' />
                            </Link>

                        </li>
                        <li>
                            <Link className='logoutbtn' to='/' onClick={userState}>
                                <i class="fa-solid fa-right-from-bracket"></i>
                            </Link>
                        </li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    )
}

export default Navbar
