import React, { useEffect, useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './login.css';

function Login() {

  const [usersData, setUsersData] = useState([]);
  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();

  useEffect(() => {
    Axios.get('http://localhost:4000/api/users').then(response => {
      setUsersData(response.data);
    });
  }, []); 

  const checkInfo = () => {
    const inputMatch = usersData.some(element => (element.username === usern && element.password === pwd));

    if(inputMatch){
      window.location.href = "/";
    } else {
      document.getElementById("login-error").style.color = "red";
    }
  }

  return (
    <>
    <h1>Welcome Back</h1>
    <h4 id='login-error'>Invalid username or password</h4>
    <div className='login-form'>
      <form>
        <label>Username</label><br/>
        <input type="text" name="username" onChange={(e => setUsername(e.target.value))} required /><br/>
        <label>Password</label><br/>
        <input type="password" name="password" onChange={(e => setPwd(e.target.value))} required /><br/>
        <h5>Don't have an account? <Link to='/signup'>Sign Up Here</Link></h5>
        <button type="button" onClick={checkInfo}>Submit</button>
      </form>
    </div>
    </>
  )
}

export default Login
