import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import $ from 'jquery';

function Mylog() {

  const [searchKey, setSearchKey] = useState("");
  const [bookData, setBookData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [bookId, setBookId] = useState();
  const usern = localStorage.getItem("user");

  useEffect(() => {
    Axios.post('http://localhost:4000/login', {
      usern: usern,
    }).then((response) => {
      if(response.data.message){
        document.getElementById("no-log").innerHTML = response.data.message;
        document.getElementById("no-log").style.textAlign = "center";
      } else { 
          loadLogs();
      }
    });
  }, []);

  const loadLogs = () => {
    Axios.get('https://www.googleapis.com/books/v1/volumes?q=subject:fiction&filter=ebooks&key=AIzaSyDQ8kCRJpt7BCr2_WoshbW57wBBd_ppMFE'+'&maxResults=40')
      .then(res=> setBookData(res.data.items)); 
      setIsLoading(false)
      console.log(bookData);
  }

  function togglePopup(text) {
    document.getElementById("book-popup").innerHTML = text;

    $("#book-popup").fadeIn(1000);
    $("#book-popup").fadeOut(1000);
  }

  const logBook = () => {
      Axios.post('http://localhost:4000/logbook', {

        bookId: bookId,
      }).then((response) => {
        if(response.data.message){
          console.log(response.data.message);
        } else {
          togglePopup('Book Removed');
        }
      })
  }

  // const removeBook = () => {
  //     Axios.post('http://localhost:4000/logbook', {
  //       usern: usern,
  //       bookId: bookId,
  //     }).then((response) => {
  //       if(response.data.message){
  //         console.log(response.data.message);
  //       } else {
  //         document.getElementById("logbtn").innerHTML = 'Log';
  //         togglePopup('Book Removed');
  //       }
  //     })
  // }

  function on() {
    document.getElementById("overlay").style.display = "block";
    document.body.classList.add('disable-scroll');
  };

  function off() {
    document.getElementById("overlay").style.display = "none";
    document.body.classList.remove('disable-scroll');
  };

  if(isLoading){
    return(
      <div className='loading'>Loading...</div>
    )
  }

  return (
    <div className='book-page'>
      <div id="overlay">
          {bookData.filter(book => book.id === bookId).map(filteredBook => (
            <div key={filteredBook.id} className="overlay-inner">

              <img alt='Book cover' src={filteredBook.volumeInfo.imageLinks && filteredBook.volumeInfo.imageLinks.thumbnail}/>
              <div className='details'>
                <h1>{filteredBook.volumeInfo.title} <button className="close" onClick={off}><i className="fa-solid fa-circle-xmark"></i></button></h1><br/>
                <h3>Author: {filteredBook.volumeInfo.authors}</h3><br/>
                <h3>Summary:</h3>
                <p>
                  {filteredBook.volumeInfo.description}
                </p>
                <button id='logbtn' onClick={logBook}>Remove from Log</button>
              </div>
            </div>
          ))} 
      </div>

      <h1 id='no-logs'></h1>
      <div className='book-results'>
        {
          bookData.map((book) => {
            let cover = book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail;

            if(cover !== undefined){
              return (
                <img key={book.id} src={cover} alt='Book cover' onClick={()=> {on(); setBookId(book.id)}} /> 
              )
            }
          }) 
        }
      </div>


    </div>
  )
}

export default Mylog
