import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import './Profile.css';

function Profile() {

  const email_format = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
  const user_format = /^[a-zA-Z0-9]{3,15}$/;
  const pwd_format = /^[a-zA-Z0-9]{8,20}$/;

  const [email, setEmail] = useState();
  const [usern, setUsername] = useState();
  const [pwd, setPwd] = useState();
  const [confirmPwd, setConfirmPwd] = useState();

  const [focusEmail, setFocusEmail] = useState();
  const [focusUsern, setFocusUsern] = useState();
  const [focusPwd, setFocusPwd] = useState();
  const [matchPwd, setMatchPwd] = useState();
  const [update, setUpdate] = useState(false);
  const [updateStatus, setUpdateStatus] = useState(false);

  const signUser = () => {
    if((email != "" && email != null && email_format.test(email)) && (usern !== "" && usern !== null && user_format.test(usern)) && (pwd !== "" && pwd !== null && pwd_format.test(pwd) && confirmPwd === pwd)){
      Axios.post('http://localhost:4000/update', {
        email: email,
        usern: usern,
        pwd: pwd,
      }).then((response) => {
        if(response.data.message){
          document.getElementById("update-error").style.color = "red";
          setUpdateStatus(response.data.message);
        } else {
          console.log(response.data.message);
        }

        console.log(response);
      });
    } else {
      document.getElementById("update-error").style.color = "red";
      setUpdateStatus('Please fill in the form to register!');
    }
  }

  const updateInfo = () => {
    setUpdate(!update);
    document.getElementById("email").disabled = !update;
    document.getElementById("username").disabled = !update;
    document.getElementById("password").disabled = !update;
    document.getElementById("password-match").disabled = !update;
    document.getElementById("confirm").style.display = "inline";
    document.getElementById("password-match").style.display = "inline";

    if(document.getElementById("submit").innerHTML === "Update"){
      document.getElementById("submit").innerHTML = "Submit";
    } else {
      document.getElementById("submit").innerHTML = "Update";
    }

  }

  return (
    <div className='profile-form'>
      <img className='profile-icon' src={require('../assets/images/user-icon.png')} alt='profile-icon' /><br/>
      <h1>Profile Information</h1><br/><br/>
      <h4 id='update-error'>{updateStatus}</h4>
      <form>
        <label>Email <span id='email-error'>[invalid email]</span></label><br/>
        <input id='email' type="text" name="email" onFocus={() => {setFocusEmail(true)}} onBlur={() => {setFocusEmail(false)}} onChange={(e => setEmail(e.target.value))} disabled/><br/>
        <label>Username <span id='username-error'>[3-15 characters]</span></label><br/>
        <input id='username' type="text" name="username" onFocus={() => {setFocusUsern(true)}} onBlur={() => {setFocusUsern(false)}} onChange={(e => setUsername(e.target.value))} disabled/><br/>
        <label>Password <span id='pass-error'>[8-20 characters]</span></label><br/>
        <input id='password' type="password" name="password" onFocus={() => {setFocusPwd(true)}} onBlur={() => {setFocusPwd(false)}} onChange={(e => setPwd(e.target.value))} disabled/><br/>
        <label id='confirm'>Confirm Password <span id='mpass-error'>[Must match]</span></label><br/>
        <input id='password-match' type="password" name="password-match" onFocus={() => {setMatchPwd(true)}} onBlur={() => {setMatchPwd(false)}} onChange={(e => setConfirmPwd(e.target.value))} disabled/><br/>
        <button id='submit' type='button' onClick={updateInfo}>Update</button>
      </form>
    </div>
  )
}

export default Profile
